class Solution(object):
    def rotateRight(self, head, k):
        """
        :type head: ListNode
        :type k: int
        :rtype: ListNode
        """
        if not head:
            return None
        if not head.next:
            return head
        length = 0; 
        curr = head; 
        slow = head;
        fast = head; 
        while curr:
            curr = curr.next;
            length += 1;
        k = k % length;
        while fast.next:
            fast = fast.next;
            k -= 1;
            if k < 0:
                slow = slow.next; 
            
        fast.next = head;
        ans = slow.next;
        slow.next = None;
        return ans;

```
