        """
        :type head: ListNode
        :rtype: ListNode
        """
        slow = fast = head;
        while fast and fast.next:
            fast = fast.next.next;
            slow = slow.next;
            if fast == slow:
                break;
        if fast is None or fast.next is None:
            return None; 
        fast = head; 
        while slow != fast:
            fast = fast.next;
            slow = slow.next;
        return fast; 
