class Solution(object):
    def maxDepth(self, root):
        """
        :type root: TreeNode
        :rtype: int
        """
        if root is None: 
            return 0; 
        left_max = self.maxDepth(root.left); 
        right_max = self.maxDepth(root.right);
        return 1 + max(left_max, right_max);

