class Solution(object):
    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        n = len (s);
        k = len (words);
        word_length = len(words[0]); 
        substring_size = k * word_length; 
        counter = collections.Counter(words); 
        #hexlper function to determine whether starting index i can find a valid substring
        def helper(start):
            copy = counter.copy(); 
            used = 0;
            for j in range(start, start+substring_size, word_length):
                str = s[j:j+word_length];
                if copy[str] > 0: 
                    copy[str] -= 1;
                    used += 1;
                else:
                    break;
            return used == k; 

        res = []; 
        for i in range(n-substring_size+1):
            if helper(i):
                res.append(i);
        return res; 
