class Solution(object):
    def searchInsert(self, nums, target):
        """
        :type nums: List[int]
        :type target: int
        :rtype: int
        """
        #使用双指针，注意target大于最大值和小于最小值得情况
        left = 0; 
        right = len(nums)-1;
        if target > nums[-1]:
            return len(nums);
        if target < nums[0]:
            return 0; 
        while left + 1 < right:
            mid = (left + right) // 2;
            if nums[mid] > target:
                right = mid; 
            if nums[mid] < target:
                left = mid;
            if nums[mid] == target:
                return mid; 
        if nums[left] == target:
            return left; 
        if nums[right] == target:
            return right;
        if nums[left] < target < nums[right]:
            return left + 1;
