class Solution(object):
    def maxSlidingWindow(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: List[int]
        """
        res = []; 
        queue = collections.deque(); 
        for i in range(len(nums)):
            while queue and nums[queue[-1]] < nums[i]:  #保持单调递减， 队首需要是最大的
                queue.pop(); 
            while queue and nums[queue[0]] < i-k+1: #超过K个数就
                queue.popleft();
            queue.append(i); 
            if i >= k-1: #开始计算最大值
                res.append(nums[queue[0]]);
        return res; 
#time complexity: O(n)
