 *         this.left = left;
 *         this.right = right;
 *     }
 * }
 */
class Solution {
    public boolean isSameTree(TreeNode p, TreeNode q) {
        if(p==null&&q==null){
            return true;
        }
        if((p==null&&q!=null)||(q==null&&p!=null)){
            return false;
        }
        //判断根节点
        if(p.val==q.val){
            if(isSameTree(p.left,q.left)){
                return isSameTree(p.right,q.right);
            }else{
                return false;
            }

        }else{
            return false;
        }

    }
}

