 * }
 */
public class Codec {

  public String serialize(TreeNode root) {
        return addStr(root, new StringBuilder()).toString();
    }

    public TreeNode deserialize(String data) {
        String[] strings = data.split(",");
        LinkedList<String> strings1 = new LinkedList<>(Arrays.asList(strings));
        return rdeserialize(strings1);
    }

    public StringBuilder addStr(TreeNode root, StringBuilder str) {
        if(root==null){
            return str.append("NULL,");
        }else{
            str.append(root.val).append(",");
            str = addStr(root.left,str);
            str = addStr(root.right,str);
            return str;
        }
    }

    public TreeNode rdeserialize(List<String> dataList) {
        //如果当前节点为NULL,表示空树
        String node = dataList.remove(0);
        if ("NULL".equals(node)){
            return null;
        }
        TreeNode treeNode = new TreeNode(Integer.valueOf(node));
        treeNode.left = rdeserialize(dataList);
        treeNode.right = rdeserialize(dataList);
        return treeNode;
    }
}

