class Solution {
 int toMinus(String time){
        String[] split = time.split(":");
        int result = Integer.valueOf(split[0])*60+ Integer.valueOf(split[1]);
        return result;
    }

    public int numberOfRounds(String loginTime, String logoutTime) {

         int start = toMinus(loginTime);
        int end = toMinus(logoutTime);
        //追加分钟
        if (start>end){
            end+=24*60;
        }
        //获取以15为单位Math.floor(m2 / 15) - Math.ceil(m1 / 15);
        start =(int)Math.floor(start/15);
        end =(int)Math.ceil(end/15);
        return (end-start);
    }
}

