class Solution:
    def sortedListToBST(self, head: Optional[ListNode]) -> Optional[TreeNode]:

        def length(node):
            cnt = 0            
            while node:
                cnt += 1
                node = node.next               
            return cnt

        def inorder(l, r):
            if l > r:
                return None

            mid = (l + r) // 2
            left = inorder(l, mid - 1)
            root = TreeNode(self.head.val)
            self.head = self.head.next

            root.left = left
            root.right = inorder(mid + 1, r)

            return root

        self.head = head

        n = length(head)

        return inorder(0, n - 1)


