            self.size -= 1
            if self.size > 0:
                self.add[-1] += i
            return v+i
        else:
            return -1

    def increment(self, k: int, val: int) -> None:
        n = min(k, len(self.stack))
        if n >= 1:
            self.add[n-1] += val
