class Solution:
    def swapPairs(self, head: Optional[ListNode]) -> Optional[ListNode]:
        if not head:
            return head
        if not head.next:
            return head
        
        old_head = head
        acc = 1
        while old_head.next:
            if acc % 2 != 0:
                # need swap
                if acc == 1:
                    next_node = old_head.next
                    old_head.next = next_node.next
                    next_node.next = old_head
                    result = next_node
                    pre_node = next_node
                else:
                    next_node = old_head.next
                    old_head.next = next_node.next
                    next_node.next = old_head
                    pre_node.next = next_node
                    pre_node = next_node
            else:
