class maxQueue:

    def __init__(self):
        self.queue = collections.deque()
    def push(self, val):
        # 每次push进value的时候，把比他更小的都pop出去
        while self.queue and val>self.queue[-1]:
            self.queue.pop()
        self.queue.append(val)
    def pop(self):
        # 因为是monotonic max queue，所以popleft就是最大值
        self.queue.popleft()
    def max(self):
        return self.queue[0]


class Solution:
    def maxSlidingWindow(self, nums: List[int], k: int) -> List[int]:
        if k==1:
            return nums

        n = len(nums)
        ans= []
        q = maxQueue()

        for i in range(n):
            q.push(nums[i])
            # 到达sliding window size 的 maximum
            if i-k+1>=0:
                ans.append(q.max())
                # 如果queue的最大值等于接下来push进来的值，pop掉他，append新值可以和后面的比较
                if nums[i-k+1] == q.max(): q.pop()

        return ans
