class Solution:
    def mySqrt(self, x: int) -> int:

        if x<2:
            return x

        left,right = 0,x

        while left<=right:
            mid = left +(right-left)//2

            if mid*mid>x:
                right = mid-1
            elif mid*mid<x:
                left = mid+1
            elif mid*mid==x:
                return mid

        return right
