        def topo2_sort(x):
            vis[x] = 1
            for a in beforeItems[x]:
                if vis[a] == 0 and not topo2_sort(a):
                    return False
                elif vis[a] == 1:
                    return False
            vis[x] = 2
            topo2.append(x)
            return True
        for i in range(n):
            if vis[i] == 0:
                if not topo2_sort(i):
                    return []
        for x in topo2:
            ret[group_inter[x]].append(x)        
        ans = []
        for x in topo1:
            ans.extend(ret[x])
        return ans
