    int maxHeight = 0;
    void dfs( TreeNode *node, int curHeight){
        if( !node->left && !node->right ){
            if( curHeight > maxHeight) {
                 targetNode = node;
                 maxHeight = curHeight;
            }
            return;
        }
        if( node->left )    dfs( node->left, curHeight+1 );
        if( node->right )   dfs( node->right, curHeight+1 );
    }
    int findBottomLeftValue(TreeNode* root) {
        dfs( root, 1 );
        return targetNode->val;
    }
};

