int subarraysDivByK(vector<int>& nums, int k) {        
    map<int, int> m;
    int cnt = 0, currSum = 0;

    for (int i : nums)
    {
        currSum += i;
        int temp = currSum % k;
        if (temp < 0)
            temp += k;

        if (temp == 0)
            cnt++;

        if (m.find(temp) != m.end())
