class CustomStack {
public:
    CustomStack(int maxSize) {
        stack.resize(maxSize);
        top = maxSize;
        now = -1;
    }

    void push(int x) {
        if (now < top-1){
            ++now;
            stack[now] = x;
        }
    }
    
    int pop() {
        if (-1 == now) return -1;
        int popNum = stack[now];
        --now;
        return popNum;
    }
    
    void increment(int k, int val) {
        k = min(k, now+1);
        for (int i = 0; i < k; i++){
            stack[i] += val;
        }
    }
private:
    vector<int> stack;
    int top;
    int now;
};
