struct DLinkedNode {
    int key, value;
    DLinkedNode* prev;
    DLinkedNode* next;
    DLinkedNode(): key(0), value(0), prev(nullptr), next(nullptr) {}
    DLinkedNode(int _key, int _value): key(_key), value(_value), prev(nullptr), next(nullptr) {}
};
class LRUCache {
public:
    LRUCache(int capacity) {
        head = new DLinkedNode();
        tail = new DLinkedNode();
        head->next = tail;
        tail->prev = head;
        capacitys = capacity;
    }
    
    int get(int key) {
        if (!hash[key]) return -1;
        DLinkedNode *ptr = hash[key];
        moveTohead(ptr);
        return ptr->value;
    }
    
    void put(int key, int value) {
        if (hash[key]){
            DLinkedNode *ptr = hash[key];
            ptr->value = value;
            moveTohead(ptr);
        }else{
            DLinkedNode *ptr = new DLinkedNode(key,value);
            hash[key] = ptr;
            addToHead(ptr);
            size++;
            if (size > capacitys){
                DLinkedNode *tmpptr = removeTail();
                hash.erase(tmpptr->key);
                size--;
            }
        }
    }
    void addToHead(DLinkedNode *node){
        node->prev = head;
        node->next = head->next;
        head->next->prev = node;
        head->next = node;
    }
    void removeNode(DLinkedNode *node){
        node->next->prev = node->prev;
        node->prev->next = node->next;
    }
    void moveTohead(DLinkedNode *node){
        removeNode(node);
        addToHead(node);
    }
    DLinkedNode *removeTail(){
        DLinkedNode *ptr = tail->prev;
        removeNode(ptr);
        return ptr;
    }
private:
    unordered_map<int,DLinkedNode*>hash;
    DLinkedNode *head;
    DLinkedNode *tail;
    int size = 0;
    int capacitys = 0;
};
```

