class Solution {
public:
    int findBottomLeftValue(TreeNode* root) {
        queue<TreeNode*> q;
        q.push(root);
        TreeNode * res = nullptr;
        while (!q.empty())
        {
            res = q.front();
            int size = q.size();
            q.pop();
            while (size--)
            {
                TreeNode *cur = q.front();
                if (cur->left)
                    q.push(cur->left);
                if (cur->right)
                    q.push(cur->right);
            }
        }
        return res->val;
    }
};
