class Solution {
public:
    vector<int> topKFrequent(vector<int>& nums, int k) {
        map<int,int>mp;
        for (auto &num : nums){
            mp[num]++;
        }
        priority_queue<pair<int,int>,vector<pair<int,int>>,greater<pair<int,int>>>q;
        for(auto a:mp){
            if(q.size() == k){
                if (q.top().first<a.second){
                    q.pop();
                    q.push(make_pair(a.second,a.first));
                }
            }else{
                q.push(make_pair(a.second,a.first));
            }
        }
        vector<int> ret;
        while(!q.empty()){
            ret.push_back(q.top().second);
            q.pop();
        }
        return ret;
    }
};
