public:
    int lengthOfLongestSubstring(string s) {
        int ansMax = 0;
        int l = 0, r = 0;
        unordered_map<char,int>map;
        while(r < s.size()){
            if(map.count(s[r]) > 0){
                int lastPost = map[s[r]];
                if(lastPost>=l && lastPost<=r)
                    l = lastPost +1;
