class Solution {
public:
    vector<int> findSubstring(string s, vector<string>& words) {
        vector<int> res;
        if (words.size() == 0)
            return res;
        int wlen = words[0].size();
        int slen = s.size();
        int count = words.size();
        map<string, int>mp;
        for(auto it : words)
            mp[it]++;
        
        for (int i=0;i<slen-(wlen*count)+1;i++){
            string cur=s.substr(i,wlen*count);
            map<string,int>tmp;
            int j=0;
            for (;j<cur.length();j+=wlen){
                string word = cur.substr(j,wlen);
                if (0 == mp.count(word))
                    break;
                tmp[word]++;
                if(tmp[word] > mp[word]){
                    break;
                }
            }
            if(j == cur.length())
                res.push_back(i);
        }
        return res;
    }
};
