class Solution:
    def maxChunksToSorted(self, arr: List[int]) -> int:
        res = []
        # 单调增栈
        for i in arr:
            if not res or i >= res[-1]:
                res.append(i)
            else:
                max = res.pop()
                while res and i< res[-1]:
                    res.pop()
                res.append(max)
        return len(res)
