    const res=[];
    let l=0,r=0,pos=0;
    let length=s.length;
    for (let i = 0; i < length; i++) {
        let array_abs=0;
        l=r=pos=i;
        for (let j = r; j < length; j++) {
            if (s[j]===c) {
                r=j;
                break;
            }
