var rotateRight = function(head, k) {
    if (!head||!head.next) return head;
    while (k) {
        let temp=head.next;
        let pre=head;
        while (temp.next){
            temp=temp.next;
            pre=pre.next
        }
        temp.next=head;
        pre.next=null;
        head=temp;
        k--;
    }
    return head;
};
