var serialize = function(root) {
    if(!root) return '#';
    // 先序遍历
    const value = root.val;
    const leftValue = serialize(root.left);
    const rightValue = serialize(root.right);
    const str =  `${value},${leftValue},${rightValue}`;
    return str;
};

/**
 * Decodes your encoded data to tree.
 *
 * @param {string} data
 * @return {TreeNode}
 */
var deserialize = function(data) {
    // 这个有一个小技巧，数组翻转一下，就不用再取数组最前面的一个值了，
    // 翻转前执行1000ms+,翻转后100ms-
    const dataArray = data.split(',').reverse();
    const transform = (nodes) =>{
        if(nodes.length === 0) return null;
        const value = nodes.pop();
        if(value === '#') return null;
        let root = new TreeNode(parseInt(value));
        root.left = transform(nodes);
        root.right = transform(nodes);
        return root;
    };
    return transform(dataArray);
};

