var getOrder = function(tasks) {
    if (tasks.length===1) return [0];
    for (let i = 0; i < tasks.length; i++) {
        tasks[i].unshift(i);
    }
    tasks=tasks.sort((a,b)=>{
        return a[2]!==b[2]?a[2]-b[2]:a[0]-b[0];
    })
    let res=[];

    let queue=[];
    let time=1,i=0,sortId=0;
    while (sortId<tasks.length){
        while (i<tasks.length && tasks[i][1]<=time) {
            queue.unshift(tasks[i]);
            i++;
        }
        if (queue.length===0) time=tasks[i][1];
        else {
            let cur=queue.shift();
            res.push(cur[0]);
            sortId++;
            time+=cur[2];
