// 维护一个栈
var decodeString = function (s) {
  const stack = [];
  for (let i = 0; i < s.length; i++) {
    if (s[i] != "]") {
      stack.push(s[i]);
      continue;
    }
    let str = "";
    let cur = stack.pop();
    while (cur != "[") {
      // 加在当前的后面
      str = cur + str;
      cur = stack.pop();
    }
    let num = "";
    // 排除 "["
    cur = stack.pop();
    // 对于数字
    while (!isNaN(cur)) {
      // 字符串相加
      num = cur + num;
      cur = stack.pop();
    }
    // 字母 或者 "["
    stack.push(cur);
    stack.push(str.repeat(num));
  }
  return stack.join("");
