var lengthOfLongestSubstring = function (s) {
  let l = 0;
  let res = 0;
  const map = new Map();
  for (let j = 0; j < s.length; j++) {
    // map.get(s[j]) >= l 保证重复元素在区间里
    if (map.has(s[j]) && map.get(s[j]) >= l) {
      // 以重复位置+1作为左边界
      l = map.get(s[j]) + 1;
    }
    res = Math.max(res, j - l + 1);
    map.set(s[j], j);
  }
  return res;
};
