var middleNode = function (head) {
  let dummy = new ListNode(0);
  dummy.next = head;
  let slow = dummy,
    fast = dummy;
  while (fast && fast.next) {
    fast = fast.next.next;
    slow = slow.next;
  }
  // 对于偶数个，中间节点有两个，返回靠后的节点
  return fast == null ? slow : slow.next;
};
