var mySqrt = function (x) {
  if (x < 2) return x;
  let l = 1,
    mid,
    r = x;
  while (l <= r) {
    mid = (l + (r - l) / 2) >> 0;
    if (mid * mid == x) {
      return mid;
    } else if (mid * mid < x) {
      l = mid + 1;
    } else {
      r = mid - 1;
    }
  }
  return r > x / r ? l : r;
};
