    string decodeString(string s) {
        string result;
        int num;
        stack<int> numbers;
        stack<string> strs; 
        for(int i=0;i<s.size();i++)
        {
            if(s[i] <= '9' && s[i] >= '0')
            {
                num = num * 10 + s[i] - '0';
            }
            else if(s[i] >= 'a' && s[i] <= 'z')
            {
                result += s[i];
