            else if(s[i] == '[')
            {
                numbers.push(num);
                num = 0;
                strs.push(result);
                result = "";
            }
            else
            {
                int loop = numbers.top();
                numbers.pop();
                for(int j = 0; j<loop; j++)
                {
                    strs.top() += result;
                }
                result = strs.top();
                strs.pop();
            }
        }
        return result;
    }
