class Solution {
public:
    ListNode* rotateRight(ListNode* head, int k) {
        if(head == nullptr)
            return head;

        if(head->next == nullptr)
            return head;

        if(k == 0)
            return head;

        vector<ListNode *> nodes;
        while(head != nullptr)
        {
            nodes.push_back(head);
            head = head->next;
        }
        if(nodes.size() == k)
        {
            return nodes[0];
        }
        else
        {
            while(nodes.size() <= k)
                k -= nodes.size();

            if(k == 0)
                return nodes[0];

            if(nodes.size() > k)
            {
                nodes[nodes.size() - 1]->next = nodes[0];
                head = nodes[nodes.size() - k];
                nodes[nodes.size() - k - 1]->next = nullptr;
            }
        }
        return head;
    }
};
