class Solution {
public:
    ListNode* swapPairs(ListNode* head) {
        if(!head)
            return head;

        if(!head->next)
            return head;

        int n = 0;
        ListNode *iter = head;
        while(iter->next != nullptr){
            iter = iter->next;
            n++;
        }

        ListNode* node = new ListNode();
        node = head;
        head = head->next;
        node->next = head->next;
        head->next = node;
        ListNode* result = new ListNode();
        result = head;
        head = node->next;

        ListNode* preNode = new ListNode();
        preNode = node;

        for(int i = 0; i<n; i++)
        {
            if(!head)
                return result;
            if(!head->next)
                return result;
            ListNode* node = new ListNode();
            node = head;
            head = head->next;
            node->next = head->next;
            head->next = node;
            preNode->next = head;
            preNode = node;
            if(node->next)
                head = node->next;
            else
                break;
        }
        return result;
    }
};
