class LRUCache {
public:
    LRUCache(int capacity) {
        mCapacity = capacity;
    }

    int get(int key) {
        auto iter = mHash.find(key);
        if(iter == mHash.end())
            return -1;

        mCache.splice(mCache.begin(), mCache, iter->second);
        return iter->second->second;
    }

    void put(int key, int value) {
        auto iter = mHash.find(key);
        if(iter != mHash.end())
        {
            iter->second->second = value;
            mCache.splice(mCache.begin(), mCache, iter->second);
            return;
        }
        else
        {
            mCache.push_front({key, value});
            mHash[key] = mCache.begin();

            if(mHash.size() > mCapacity)
            {
                mHash.erase(mCache.back().first);
                mCache.pop_back();
            }
        }
    }

private:
    std::unordered_map<int, list<pair<int, int>>::iterator> mHash;
    list<pair<int,int>> mCache;
    int mCapacity;
};
