        if(!node->left && !node->right)
        {
            if(depth > mCurDepth)
            {
                mCurDepth = depth;
                mTargetNode = node;
            }
            return;
        }
        if(node->left)
            compute(node->left, depth + 1);
        if(node->right)
            compute(node->right, depth + 1);
    }

    int findBottomLeftValue(TreeNode* root)
    {
        compute(root, 1);
        return mTargetNode->val;
    }

private:
    TreeNode *mTargetNode;
    int mCurDepth = 0;
};
