        auto mid = beg + (end - beg) / 2;
        while(mid != end)
        {
            if(*mid == target)
                return mid - nums.begin();
            else if(*mid < target)
                beg = mid + 1;
            else
                end = mid;
            mid = beg + (end - beg) / 2;
