class Solution {
public:
    const static int MAXN = 100005;
    int tree[MAXN<<2];
    int N;
    void build(vector<int>* p,int v,int l,int r) {
        if(l == r) tree[v] = (*p)[l];
        else {
            int mid = (l+r) >>1;
            build(p,v<<1,l,mid),build(p,v<<1 | 1,mid+1,r);
            tree[v] = max(tree[v<<1],tree[v<<1 | 1]);
        }
    }
    int query(int v,int lb,int rb,int ql,int qr) {
        if(ql > qr) return INT32_MIN;
        if(ql == lb && qr == rb) return tree[v];
        int mid = (lb+rb) >> 1;
        return max(query(v<<1,lb,mid,ql,min(qr,mid)),
            query(v<<1 | 1, mid+1,rb, max(mid+1,ql),qr)
        );
    }

    vector<int> maxSlidingWindow(vector<int>& nums, int k) {
        if(k == 1) return nums;
        N = nums.size();
        if(k >= N) return {*max_element(begin(nums),end(nums))};
        int lim = N-k+1;
        vector<int> ans(lim);
        build(&nums,1,0,N-1);
        for(int i=0;i<lim;i++) {
            ans[i] = query(1,0,N-1, i,i+k-1);
        }        
        return ans;
    }
};
