class Solution:
    def maxSlidingWindow(self, nums: List[int], k: int) -> List[int]:
        window = []
        res = []
        for i, v in enumerate(nums):
            if i >= k and window[0] <= i-k: window.pop(0)
            while window and nums[window[-1]] <= v: window.pop()
            window.append(i)
            if i >= k-1: res.append(nums[window[0]])
        return res
