class Solution:
    def topKFrequent(self, nums: List[int], k: int) -> List[int]:
        """
        Ideas: sort + hash

        TC: O(nlogn)
        SC: O(n)
        """
        count_map = {}
        for num in nums:
            count_map[num] = count_map.get(num, 0) + 1

        count_array = []
        for key, v in count_map.items():
            count_array.append((v, key))

        count_array.sort(key=lambda x: x[0], reverse=True)
        ans = []
        for i in range(k):
            ans.append(count_array[i][1])

        return ans

    def topKFrequent(self, nums: List[int], k: int) -> List[int]:
        """
        Ideas:
            hash count + bucket sort
        TC: O(n)
        SC: O(n)
        """
        bucket = [[] for _ in range(len(nums) + 1)]
        counter = {}
        for num in nums:
            counter[num] = counter.get(num, 0) + 1

        for num, count in counter.items():
            bucket[count].append(num)

        ans = []

        for i in range(len(bucket) - 1, -1, -1):
            if k == 0:
                break
            if bucket[i] != []:
                ans += bucket[i]
                k -= len(bucket[i])
        return ans

    def topKFrequent(self, nums: List[int], k: int) -> List[int]:
        """
        Ideas:heap sort
            hash + min heap (keep size k)
        Other ideas: quick select -> may do it later
        TC: O(nlogn)
        SC: O(n)
        """
        import heapq
        heap = []
        counter = {}
        for num in nums:
            counter[num] = counter.get(num, 0) + 1
        for num, count in counter.items():
            # print(num, count)
            heapq.heappush(heap, (count, num))
            if len(heap) > k:
                heapq.heappop(heap)
        ans = []
        for _ in range(k):
            ans.append(heapq.heappop(heap)[1])
        return ans




