var detectCycle = function(head) {
  if (head === null || head.next === null) return null;
  let fast = head, slow = head;
  let count = 0;
  while (fast && fast.next) {
    slow = slow.next;
    if (count === 0) fast = fast.next.next;
    else fast = fast.next;
    if (slow === fast) {
      if (head === fast) return head;
      if (count === 0) {
        fast = head;
        count++;
      } else {
        return fast;
      }
    }
  }
  return null;
};
