var maxDepth = function(root) {
  let num = 0;
  let preOrder = function(root, level) {
    if (!root) return;
    if (!root.left && !root.right) num = Math.max(num, level);
    preOrder(root.left, level + 1);
    preOrder(root.right, level + 1);
  };
  preOrder(root, 1);
  return num;
};
```

