};

let preorder = function(p, q) {
  if (p === null && q === null) return true;
  if (p === null || q === null || p.val !== q.val) return false;
  let left = preorder(p.left, q.left);
  if (!left) return false;
  return preorder(p.right, q.right);
};
```

