class Solution {
    solve(nums) {
        let ans = 0;
        let sorted = [];
        for (let i = 0; i < nums.length; i++) {
            let left = find(sorted, nums[i] * 3);
            ans += i - left;
            let insert = find(sorted, nums[i]);
            sorted.splice(insert, 0, nums[i]);
        }

        function find(sortedNumbers, target) {
            let left = 0;
            let right = sortedNumbers.length;
            while (left <= right) {
                let mid = left + Math.floor((right - left) / 2);
                if (sortedNumbers[mid] <= target) {
                    left = mid + 1;
                }else {
                    right = mid - 1;
                }
            }
            return left;
        }

        return ans;
    }
}
