class Solution:
    def solve(self, nums, k):
        s = sum(nums)
        if s%k==0:
            return 0
        dic = {0:-1}
        mod = s%k
        total = 0
        ans = len(nums)
        for i in range(len(nums)):
            total += nums[i]
            cur = total % k
            target = (cur -mod +k)%k
            if target in dic:
                ans = min(ans, i - dic[target])
            dic[cur] = i
