CustomStack.prototype.pop = function() {
  if (this.stack.length === 0) {
    return -1;
  } 
  const inc = this.map.get(this.stack.length) || 0;
  this.map.set(this.stack.length - 1, (this.map.get(this.stack.length - 2) || 0) + inc);
  this.map.set(this.stack.length, 0);
  return this.stack.pop() + inc;
}

CustomStack.prototype.increment = function(k, val) {
  const index = Math.min(k, this.stack.length) - 1;
  if (index < 0) {
    return;
  }
  this.map.set(index, (this.map.get(index) || 0) + val);
}
