var maxChunksToSorted = function(arr) {
    let stack = [arr[0]];
    const n = arr.length;
    for (let i = 1; i < n; i++) {
        const cur = arr[i];
        if (cur >= stack[stack.length - 1]) {
            stack.push(cur);
        } else {
            const top = stack.pop();
            while (cur < stack[stack.length - 1]) {
