var swapPairs = function(head) {
    if (head == null || head.next == null) {
        return head;
    }
    const dummy = new ListNode(-1);
    dummy.next = head;
    let cur = dummy;

    while (cur.next && cur.next.next) {
        let p1 = cur.next;
        let p2 = cur.next.next;
        let next = cur.next.next.next;

        cur.next = p2;
        p2.next = p1;
        p1.next = next;

